% Search for a lexical unit of a given grammatical category
% Available categories: V, N, C, CL, P, I, PONCT, A, ADV, PRO, D, ET, P+D, P+PRO, PREF

match { N [cat="P+PRO"] }

% Search for causative auxiliaries ([aux.caus])
% Warning: in deep-sequoia, relation names may be prefixed (with &quot;S:&quot; for surface and with &quot;D:&quot; for deep relations)

match {
  GOV [];
  DEP [];
  GOV -[S:aux.caus]-> DEP
}

% Search for an 8-cycle

match {
  N1[]; N2[]; N3[]; N4[]; N5[]; N6[]; N7[]; N8[];
  N1 -> N2; N2 -> N3; N3 -> N4; N4 -> N5; N5 -> N6; N6 -> N7; N7 -> N8; N8 -> N1;
   N1.position < N2.position;
   N1.position < N3.position;
   N1.position < N4.position;
   N1.position < N5.position;
   N1.position < N6.position;
   N1.position < N7.position;
   N1.position < N8.position;
}

% Search for 7 governors

match {
  N[];
  N1[]; N2[]; N3[]; N4[]; N5[]; N6[]; N7[];
  N1 -> N; N2 -> N; N3 -> N; N4 -> N; N5 -> N; N6 -> N; N7 -> N;
   N1.position < N2.position;
   N2.position < N3.position;
   N3.position < N4.position;
   N4.position < N5.position;
   N5.position < N6.position;
   N6.position < N7.position;
}
