% Search for a lexical unit of a given grammatical category
% Available categories: ".", ADJ, ADP, ADV, CONJ, DET, NOUN, NUM, PRON, VERB, X

match { N [cat="NUM"] }

% Search for a dependency relation
% Available relations are:
% adpmod, adpobj, advcl, advmod, amod, appos, cc, ccomp,
% compmod, conj, csubj, dep, det, dobj, iobj, mark, mwe,
% neg, nmod, nsubj, nsubjpass, num, p, poss, rcmod, xcomp
match {
  GOV [];
  DEP [];
  GOV -[advcl]-> DEP
}

% Search for a "det" dependency relation
% such that the governor is neither a NOUN nor an ADV

match {
  GOV [cat <> NOUN|ADJ];
  DEP [];
  GOV -[det]-> DEP
}
