% Search for a lexical unit of a given grammatical category
% Available categories: V, N, C, CL, P, I, PONCT, A, ADV, PRO, D, ET, P+D, P+PRO, PREF

pattern { N [cat="P+PRO"] }

% Search for causative auxialiaries ([aux.caus])

pattern {
  GOV [];
  DEP [];
  GOV -[aux.caus]-> DEP
}

% Search for both a category and a relation (for instance, a preposition used as a determiner)

pattern {
  PREP [cat=P];
  X [];
  X -[det]-> PREP
}

% For instance, search for a coordination lexical unit which is not the dependent of a "coord" relation

pattern {
  CC [cat=C, s=c]
}
without {
  X [];
  X -[coord|root]-> CC;
}

% Search for non-projective dependencies (punctuations are ignored)

pattern {
  % Search for two different relations
  G1 [cat<>PONCT]; D1 [cat<>PONCT]; G1 -[*]-> D1;
  G2 [cat<>PONCT]; D2 [cat<>PONCT]; G2 -[*]-> D2;

  % There are 4 ways to impose that there is a crossing: uncomment exaclty one of the 4 next lines

  % G1.position < G2.position; G2.position < D1.position; D1.position < D2.position;
  % G1.position < D2.position; D2.position < D1.position; D1.position < G2.position;
  % D1.position < G2.position; G2.position < G1.position; G1.position < D2.position;
  D1.position < D2.position; D2.position < G1.position; G1.position < G2.position;
}
